package Myntra;

public class NumericPatters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//pattern 1//
		int i,j,k;
		int n=5;
		for(i=1;i<=n;i++)
		{
			for (j=1;j<=i;j++)
			{
				System.out.print(j);
			}
			System.out.println("");
		}
		//Pattern 2//
		for(i=1;i<=n;i++)
		{
			for (j=1;j<=i;j++)
			{
				System.out.print(i);
			}
			System.out.println("");
		}
		

	}

}
